// Extract input from AWS Lambda trigger
const body = $input.item.json.body || $input.item.json;

return {
  projectId: body.projectId,
  s3Bucket: body.s3Bucket || 'sliderx-uploads-dev',
  s3Key: body.s3Key,
  projectBrief: body.projectBrief || {}
};

// Prepare AI prompt for initial review
const extractedData = $input.item.json;
const projectData = $('Parse Lambda Input').first().json;

const systemPrompt = `You are a presentation strategist trained in Nancy Duarte and Dan Roam principles.

Your task is to:
1. Generate a per-slide summary of the presentation
2. Identify 2-4 clarifying questions to ask the user before creating the final 3-slide summary

Output JSON format:
{
  "summary": [
    {"slideNumber": 1, "title": "slide title", "keyPoints": "2-3 sentence summary"},
    {"slideNumber": 2, "title": "slide title", "keyPoints": "2-3 sentence summary"}
  ],
  "reviewAndRefine": {
    "questions": [
      {"question": "What is your target audience's technical level?", "type": "text"},
      {"question": "What is the desired outcome of this presentation?", "type": "text"}
    ]
  }
}`;

const projectBrief = projectData.projectBrief;
const userPrompt = `PRESENTATION CONTENT:
${extractedData.fullText}

BUSINESS CONTEXT:
Purpose: ${projectBrief.businessPurpose || 'Not specified'}
Project Phase: ${projectBrief.projectPhase || 'Not specified'}
Key Metrics: ${projectBrief.keyMetrics || 'Not specified'}
Current Blockers: ${projectBrief.currentBlockers || 'Not specified'}

Pages: ${extractedData.pageCount}

Please:
1. Create a per-slide summary (title + 2-3 sentence summary of key points)
2. Ask 2-4 clarifying questions that would help create a better 3-slide executive summary`;

return {
  projectId: projectData.projectId,
  systemPrompt: systemPrompt,
  userPrompt: userPrompt
};

// Parse AI response
const item = $input.item.json;
const projectId = $('Prepare Stage 1 Prompt').first().json.projectId;

try {
  let content = item.choices[0].message.content;

  // Remove markdown code blocks if present
  content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  const parsed = JSON.parse(content);

  if (!parsed.summary || !parsed.reviewAndRefine) {
    throw new Error('Missing required fields in AI response');
  }

  return {
    projectId: projectId,
    summary: parsed.summary,
    reviewAndRefine: parsed.reviewAndRefine
  };

} catch (error) {
  console.error('Parse error:', error);
  throw new Error(`Failed to parse AI response: ${error.message}`);
}

