// Parse User Answers - Extract complete context
const body = $input.item.json.body || $input.item.json;

return {
  projectId: body.projectId,
  projectBrief: body.projectBrief,
  reviewAndRefine: body.reviewAndRefine,
  summary: body.summary,
  userAnswers: body.userAnswers || body.answers,
  timestamp: new Date().toISOString()
};

// Validate Stage 2 Input - Check all required context
const data = $input.item.json;

const validation = {
  hasProjectId: !!data.projectId,
  hasProjectBrief: !!data.projectBrief && typeof data.projectBrief === 'object',
  hasReviewAndRefine: !!data.reviewAndRefine,
  hasSummary: !!data.summary && Array.isArray(data.summary),
  hasUserAnswers: !!data.userAnswers && Array.isArray(data.userAnswers)
};

const isValid = validation.hasProjectId && validation.hasProjectBrief && 
                validation.hasReviewAndRefine && validation.hasSummary && 
                validation.hasUserAnswers;

if (!isValid) {
  throw new Error(`Stage 2 validation failed: ${JSON.stringify(validation)}`);
}

return {
  ...data,
  validation,
  validatedAt: new Date().toISOString()
};

// Prepare Final AI Prompt with all context
const data = $input.item.json;

const systemPrompt = `You are a presentation strategist trained in Nancy Duarte and Dan Roam principles.

Your task is to create a final 3-slide executive presentation.

PRINCIPLES:
1. Decision-first structure (executives start with conclusion)
2. Visual over verbal (images process 60,000x faster than text)
3. One idea per slide
4. No jargon
5. Specific visual descriptions

OUTPUT FORMAT (JSON):
{
  "slide1": {
    "title": "THE PROBLEM",
    "visual": "[Specific visual description - e.g., 'Bar chart showing 40% cost increase over 3 years vs industry 15% average']",
    "sentence": "[Max 15 words - the core problem statement]"
  },
  "slide2": {
    "title": "THE SOLUTION",
    "visual": "[Specific visual description]",
    "sentence": "[Max 15 words - the solution]"
  },
  "slide3": {
    "title": "THE ASK",
    "visual": "[Specific visual description - e.g., 'Timeline showing $2M investment returning $5M over 18 months']",
    "sentence": "[Max 15 words - clear call to action]"
  }
}`;

// Build comprehensive user prompt
const brief = data.projectBrief;
const qaContext = data.userAnswers && data.userAnswers.length > 0 
  ? data.userAnswers.map((ans, i) => `Q${i+1}: ${ans}`).join('\n')
  : 'No additional context provided';

const summaryText = data.summary.map(s => 
  `Slide ${s.slideNumber}: ${s.title}\n${s.keyPoints}`
).join('\n\n');

const userPrompt = `ORIGINAL PRESENTATION SUMMARY:
${summaryText}

BUSINESS CONTEXT:
Purpose: ${brief.businessPurpose}
Project Phase: ${brief.projectPhase}
Key Metrics: ${brief.keyMetrics}
Current Blockers: ${brief.currentBlockers}

USER ANSWERS:
${qaContext}

Based on all this context, create the final 3-slide executive presentation following the JSON format.`;

return {
  projectId: data.projectId,
  systemPrompt: systemPrompt,
  userPrompt: userPrompt,
  preparedAt: new Date().toISOString()
};

// Parse Final Slides JSON
const item = $input.item.json;
const projectId = $('Prepare Final AI Prompt').first().json.projectId;

try {
  let content = item.choices[0].message.content;
  content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  const parsed = JSON.parse(content);

  if (!parsed.slide1 || !parsed.slide2 || !parsed.slide3) {
    throw new Error('Missing required slides in AI response');
  }

  return {
    projectId: projectId,
    slides: parsed,
    parsedAt: new Date().toISOString()
  };

} catch (error) {
  console.error('Parse error:', error);
  throw new Error(`Failed to parse AI response: ${error.message}`);
}

// Validate Slides Format
const data = $input.item.json;

for (let i = 1; i <= 3; i++) {
  const slide = data.slides[`slide${i}`];
  if (!slide || !slide.title || !slide.visual || !slide.sentence) {
    throw new Error(`Slide ${i} missing required fields`);
  }
  if (slide.sentence.split(' ').length > 20) {
    console.warn(`Slide ${i} sentence exceeds 15 words`);
  }
}

return {
  ...data,
  validated: true,
  validatedAt: new Date().toISOString()
};

// Build S3 Download URL
const projectId = $('Parse Final Slides JSON').first().json.projectId;
const bucket = 'sliderx-uploads-dev';
const key = `outputs/${projectId}/final-presentation.pdf`;
const region = 'eu-central-1';

const downloadUrl = `https://${bucket}.s3.${region}.amazonaws.com/${key}`;

return {
  projectId: projectId,
  downloadUrl: downloadUrl,
  bucket: bucket,
  key: key,
  generatedAt: new Date().toISOString()
};

